@router.callback_query(F.data.in_(info_answers.keys()))
async def answer_faq(callback: CallbackQuery):
    await callback.message.edit_text(info_answers[callback.data])
    await callback.answer()

@router.callback_query(F.data == "back_to_menu")
async def back_to_faq(callback: CallbackQuery):
    await callback.message.edit_text(questions_text, reply_markup=info_keyboard)
    await callback.answer()